const SmartInput = ({
  label,
}) => {
  const [formState, setFormState] = useState({});

  const handleChange = (event) => {
    setFormState({
      pinto: event.target.value,
    });
  };

  return (
    <label className="w-100">
      {label}
      <input className="form-control" onChange={handleChange} />
    </label>
  );
};

export default SmartInput;
